// There are prettier heart formulas, but I like the simplicity of this one.

void mainImage(out vec4 c, vec2 u)
{
    u /= iResolution.y;

    vec3 a = cos(7. * (u.x + sin(iTime * vec3(.7, .8, .9))));
    vec3 b = cos(9. * (u.y + sin(iTime * vec3(.5, .6, .7))) - acos(a));

    c = vec4(smoothstep(1., 1.1, a + b), 1);
}
