void mainImage(out vec4 fcol, in vec2 fc)
{
    vec2 r  = iResolution.xy,
         sc = fc / r.xy;

    vec3 c = .5 + .5 * cos(iTime + sc.xyx + vec3(.0, 2., 4.));

    sc = (sc * 2. - 1.) * max(r.xy / r.yx, vec2(1.));

    float t = abs(length(sc) - .9) - .01,
          b = clamp(1. - t, 0., 1.),
          p = 4. / min(r.x, r.y);

    c *= sqrt(b)      * .5   +
          b * b * b   * .375 +
          pow(b, 7. ) * .45  +
          pow(b, 16.) * 1.5  +
         1.0 - smoothstep(-p, p, t);

    fcol = vec4(c * .375, 1.);
}

