void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = iResolution.xy / fragCoord;
    float rand = iDate.w;
    float rand_max = 13337.39224;
    float add = 121.673534;
    float add_max = 17.33342;

    rand = mod(rand + add * (uv.x + uv.y), rand_max);
    add = mod(rand + add, add_max);

    float xrand = mod(rand * add, rand_max);

    rand = mod(rand * add * (uv.x - uv.y), rand_max);
    add = mod(rand * add, add_max);

    float yrand = mod(rand * add, rand_max);

    vec3 color = vec3(fract(xrand + yrand));
    color = pow(color, vec3(0.4545));
    fragColor = vec4(color, 1.0);
}
