// https://www.shadertoy.com/view/MtyXRW

#define C(c) U.x-=.5; T+= U.x<.0||U.x>1.||U.y<0.||U.y>1. ?vec4(0): textureGrad(iChannel3, U/16. + fract( vec2(c, 15-c/16) / 16.), dFdx(U/16.),dFdy(U/16.) )
#define initMsg vec4 T = vec4(0)
#define endMsg  return length(T.yz)==0. ? 0. : T.x

float message(vec2 U) { // to alter in the icon with the alter message
    vec4 T = vec4(0);   // or: initMsg;
    C(74);C(117);C(108);C(105);C(111);C(32);C(105);C(110); // message "Sound in"
    return length(T.yz)==0. ? -1. : T.x; // or: endMsg;
}


// ++++++++++++++++++++++++++++++++++++++


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    if (iResolution.y<2000.) {float c=message((fragCoord/iResolution.y-vec2(.1,.2))*8.);if(c>=0.){fragColor=vec4(c);return;}}
    fragColor=vec4(0);
}

#define S(f,t) (mix(float(f(int(t))&255),float(f(int(t+1.0))&255),smoothstep(0.0,1.0,fract(t)))/128.0-1.0)

int F(int t){return (t&t/255)-(t*3&t>>15&t>>3);}

float getTime(int samp)
{
    return float(samp)/iSampleRate;
}

float bufferSound(int samp)
{
    return .5*S(F,getTime(samp)*30000.);
}

vec2 mainSound( int samp, float time )
{
    //return vec2(bufferSound(samp));

    float sum=0.;
	int itr=5;
	for(int i=0;i<itr;i++)
	{
		sum+= bufferSound(samp-i)*(1.-float(i)/float(itr));
	}
    return vec2(sum/float(itr));
}
