void mainImage(out vec4 o,vec2 fragCoord){
    o = vec4(0);
    vec2 uv = fragCoord/iResolution.xy;

    float blur = uv.x*7.;
    blur = texture(iChannel1, uv).x;// mask
    blur = 1. - blur;
    blur= blur *7.; 

    for(int i = -1;i<2;i++)
    for(int j = -1;j<2;j++)
    o += texture( iChannel0, uv + vec2(i,j)/iChannelResolution[0].xy * blur  , blur);
    o/=9.;

    //o.xyz = vec3(blur,blur,blur);
}
