void mainImage(out vec4 O, vec2 I)
{
    #define X texture(iChannel0,(p=-p*m+iTime*.2)/3e1).r
    mat2 m = mat2(1,1,-1,1);
    vec2 r=iResolution.xy,
    p=(I-r*.5)/r.y,
    a=abs(p*m);
    O=tanh(vec4(1,2,3,1)/5e1*(
    .1/abs(max(a.x-.03,abs(p.y)-.1))+
    .1/max(a.y,abs(a.x-.12)-.08))*
    (X+X+X+X+X+X));
}
