bool antialiasing = false;        // a
bool color_cyling = true;        // c
bool color_dispartment = true;  // d
bool specular_shading = true;   // h
bool only_shape = false;         // o
bool rotation_on = true;         // r
bool cross_eye_view = false;      // s
bool show_unfinished = false;    // u

// Antialiasing: change from 1 to 2 or more AT YOUR OWN RISK!
//               It may CRASH your browser while compiling!
const int aaSamples = 2;
const float aaWidth = 0.9;

const float PI  = 3.14159265359;   // Kreiszahl
const float TAU = 6.28318530718;   // 2* Pi

struct Lamp
{
