struct RenderData
{
  int objnr;   // object number
  vec3 pos;    // position 
  vec3 norm;   // surface normal
  vec3 col;    // color 
};
const int objIsBackground = 0;
const int objIsDiamond = 1;
const int objIsSky = 3;

// camera
const  vec3 camPos = vec3(0.0, 0.5,  5.1);
const  vec3 camDir = vec3(0.0,-0.1, -1.0);
const  vec3 camVup = vec3(0.0, 0.0,  1.0);
const float camFov = 4.0;
struct Camera
{   vec3 pos;   // position
    vec3 dir;   // direction
    vec3 vup;   // view up vector
    float fov;  // vertical field of view
    public Camera(fixed3 _pos, fixed3 _dir, fixed3 _vup, fixed _fov)
	{
		pos = _pos;
		dir = _dir;
		vup = _vup;
		fov = _fov;
	}
};
// camera object with default values
Camera camera1 = Camera(camPos, camDir, camVup, camFov);
vec2 mouseAngle = vec2(0);

const vec3 ambientColor = vec3(0.7);
const float ambientInt = 0.08;

const int nb_refr = 3;

const float specInt = 0.2;
const float specShin = 20.;

const float normDelta = 0.0004;
const float maxDist = 55.;

const float ior   = 2.418;
const float ior_r = 2.408;
const float ior_g = 2.424;
const float ior_b = 2.432;

vec3 diamondColor = vec3(0.98, 0.95, 0.90);

//----------------------------------------------------------
float sin1 = 0.,   cos1 = 1.;
float sin2 = 0.,   cos2 = 1.;
//----------------------------------------------------------
void map_prepare()
{
    mouseAngle = TAU * iMouse.xy / iResolution.xy;
    float angle1 =  mouseAngle.x;
    float angle2 = -mouseAngle.y;
    if (rotation_on)
    {
        angle1 += 0.3*iTime;
        angle2 += 1.0*iTime;
    }

    sin1 = sin (angle1), cos1 = cos (angle1);
    sin2 = sin (angle2), cos2 = cos (angle2);
}
//----------------------------------------------------------
float map_simple(vec3 pos)
{
    pos = vec3(pos.x, pos.y*cos2 + pos.z*sin2, pos.y*sin2 - pos.z*cos2);
    pos = vec3(pos.x*cos1 + pos.z*sin1, pos.y, pos.x*sin1 - pos.z*cos1);

    float d = 1.05;
    float s = atan(pos.y, pos.x);
    float sins = sin(s);
    float coss = cos(s);

    vec3 flatvec1 = vec3(coss, sins, 1.444);
    vec3 flatvec2 = vec3(coss, sins, -1.072);

    float  d1 = dot(flatvec1, pos) - d;               // Crown
    d1 = max(d1,dot(flatvec2, pos) - d);              // Pavillon
    d1 = max(d1,dot(vec3(0., 0., 1.), pos) - 0.35);   // Table
    return d1;
}
//----------------------------------------------------------
float map(vec3 pos)
{
    pos = vec3(pos.x, pos.y*cos2 + pos.z*sin2, pos.y*sin2 - pos.z*cos2);
    pos = vec3(pos.x*cos1 + pos.z*sin1, pos.y, pos.x*sin1 - pos.z*cos1);

    float d = 0.94;
    float b = 0.5;

    float af2 = 4./PI;
    float s = atan(pos.y, pos.x);
    float sf = floor(s*af2 + b)/af2;
    float sf2 = floor(s*af2)/af2;
    float sins = sin(sf);
    float coss = cos(sf);

    vec3 flatvec1 = vec3(coss, sins, 1.444);
    vec3 flatvec2 = vec3(coss, sins, -1.072);
    vec3 flatvec3 = vec3(cos( s), sin(s), 0);
    float csf1 = cos(sf + 0.21);
    float csf2 = cos(sf - 0.21);
    float ssf1 = sin(sf + 0.21);
    float ssf2 = sin(sf - 0.21);
    vec3 flatvec4 = vec3(csf1, ssf1, -1.02);
    vec3 flatvec5 = vec3(csf2, ssf2, -1.02);
    vec3 flatvec6 = vec3(csf2, ssf2, 1.03);
    vec3 flatvec7 = vec3(csf1, ssf1, 1.03);
    vec3 flatvec8 = vec3(cos(sf2 + 0.393), sin(sf2 + 0.393), 2.21);

    float d1 =  dot(flatvec1, pos) - d;                // Crown, bezel facets
    d1 = max(d1,dot(flatvec2, pos) - d);               // Pavillon, pavillon facets
    d1 = max(d1,dot(vec3(0., 0., 1.0), pos) - 0.3);    // Table
    d1 = max(d1,dot(vec3(0., 0.,-1.0), pos) - 0.865);  // Cutlet
    d1 = max(d1,dot(flatvec3, pos) - 0.911);           // Girdle
    d1 = max(d1,dot(flatvec4, pos) - 0.9193);          // Pavillon, lower-girdle facets
    d1 = max(d1,dot(flatvec5, pos) - 0.9193);          // Pavillon, lower-girdle facets
    d1 = max(d1,dot(flatvec6, pos) - 0.912);           // Crown, upper-girdle facets
    d1 = max(d1,dot(flatvec7, pos) - 0.912);           // Crown, upper-girdle facets
    d1 = max(d1,dot(flatvec8, pos) - 1.131);           // Crown, star facets
    return d1;
}
//----------------------------------------------------------
float trace(vec3 cam, vec3 ray, float maxdist, bool inside)
{
    float t = 4.2;
    float dist;

    // "Bounding" tracing
    if (!inside)
    {
  		for (int i = 0; i < 4; ++i)
    	{
    		vec3 pos = ray*t + cam;
    		dist = map_simple(pos);
        	if (dist > maxdist || abs(dist)<0.001)
            	break;
        	t += dist*0.95;
  		}
    }//*/

    // "Actual" tracing
  	for (int i = 0; i < 15; ++i)
    {
    	vec3 pos = ray*t + cam;
    	dist = inside ? -map(pos) : map(pos);
        if (dist > maxdist)
            break;
        t += dist * (inside ? 0.4 : 0.8);
  	}
  	return t;
}

//----------------------------------------------------------
// Old slower version (but a bit more precise)
//----------------------------------------------------------
float trace_sl(vec3 cam, vec3 ray, float maxdist, bool inside)
{
    float t = 4.;
  	for (int i = 0; i < 80; ++i)
    {
    	vec3 pos = ray*t + cam;
    	float dist = inside?-map(pos):map(pos);
        if (dist>(inside?3.:maxdist) || abs(dist)<0.001 || (inside && i>30))
            break;
        t+= dist*0.4;
  	}
  	return t;
}

//----------------------------------------------------------
// from https://www.shadertoy.com/view/MstGDM
//----------------------------------------------------------
vec3 getNormal(vec3 pos, float eps, bool inside)
{
  vec2 q = vec2(0, eps);
  return (inside ? -1. : 1.) * normalize(
    vec3(map(pos + q.yxx) - map(pos - q.yxx),
         map(pos + q.xyx) - map(pos - q.xyx),
         map(pos + q.xxy) - map(pos - q.xxy)));
}

//----------------------------------------------------------
vec3 obj_color(vec3 norm, vec3 pos)
{
  if (only_shape)  return diamondColor*0.7;  // vec3(0.35, 0.7, 1.0);
  else             return vec3(0.1);
}

//----------------------------------------------------------
vec3 sky_color(vec3 ray)
{
	vec3 rc = texture(iChannel2, ray).ggg;
//    for (int l=0; l<3; l++)
//      rc += 1.5 * normalize(lamps[l].color)
//                * lamps[l].intensity
//                * specint
//                * pow(max(0.0, dot(ray, normalize(lamps[l].position - camPos))), 200.);
    return rc;
}

//----------------------------------------------------------
// Fresnel reflectance factor through Schlick's approximation:
//   https://en.wikipedia.org/wiki/Schlick's_approximation
//----------------------------------------------------------
float fresnel(vec3 ray, vec3 norm, float n2)
{
    float n1 = 1.; // air
    float angle = clamp(acos(-dot(ray, norm)), -PI/2.15, PI/2.15);
    float r0 = pow((n1-n2)/(n1+n2), 2.);
    float r = r0 + (1. - r0)*pow(1. - cos(angle), 5.);
    return clamp(0., 0.9, r);
}

//----------------------------------------------------------
vec3 lampShading(Lamp lamp, vec3 norm, vec3 pos, vec3 ocol, bool inside)
{
	vec3 pl = normalize(lamp.position - pos);
    float dlp = distance(lamp.position, pos);
    vec3 pli = pl/pow(1. + lamp.attenuation*dlp, 2.);

    vec3 col;

    // Diffuse shading
    if (!inside)
    {
      float diff = clamp(dot(norm, pli), 0., 1.);
      col = ocol * normalize(lamp.color) * lamp.intensity 
                 * smoothstep(0., 1.04, pow(diff, 0.78));
    }

    // Specular shading
    if (specular_shading)
      if (dot(norm, lamp.position - pos) > 0.0)
        col += normalize(lamp.color) * lamp.intensity * specInt
               * pow(max(0.0, dot(reflect(pl, norm), normalize(pos - camPos))), specShin);

    return col;
}

vec3 lampsShading(vec3 norm, vec3 pos, vec3 ocol, bool inside)
{
    vec3 col = vec3(0.);
    for (int l=0; l<3; l++)   // lamps.length()
      col += lampShading(lamps[l], norm, pos, ocol, inside);

    return col;
}

//----------------------------------------------------------
vec3 GetCameraDir(vec2 fragCoord)
{
  vec2 uv = 2.0*(fragCoord.xy / iResolution.xy) - 1.0;
  uv.x *= iResolution.x / iResolution.y;

  float eyeDist = -0.02;

  if (cross_eye_view)     // stereo view ?
    if (uv.x > 0.)  
           uv.x -= 0.8; 
    else { uv.x += 0.8;  eyeDist = -eyeDist; }

  camera1.pos.x = camPos.x - eyeDist;
  camera1.dir = normalize(camDir);
  vec3 vRight = normalize(cross(vec3(0.0, 1.0, 0.0), camera1.dir));
  camera1.vup = normalize(cross(camera1.dir, vRight));

  return normalize(uv.x * vRight + uv.y * camera1.vup +  camera1.dir * camera1.fov);
}

//----------------------------------------------------------
RenderData trace0(vec3 tpos    // position
                 ,vec3 ray     // direction
                 ,bool inside) // true if inside
{
  float tx = trace(tpos, ray, maxDist, inside);
  int objnr = objIsBackground;
  vec3 pos = tpos + tx*ray;
  vec3 norm = vec3(1,0,0);
  vec3 col = vec3(0);
  if (tx < 10.)   
  {
    norm = getNormal(pos, normDelta, inside);
    if (!inside)
    {
      // Coloring
      objnr = objIsDiamond;
      col = obj_color(norm, pos) + ambientColor*ambientInt;
    }
    // Shading
    col = lampsShading(norm, pos, col, inside);
  }
  else
  {
    // Sky
    objnr = objIsSky;
    col = sky_color(ray);
  }
  return RenderData(objnr, pos, norm, col);
}

//----------------------------------------------------------
vec4 render(vec2 fragCoord, float ior)
{
  lamps[0] = Lamp(vec3(0., 4.5, 10.), vec3(1., 1., 1.), 5., 0.1);
  lamps[1] = Lamp(vec3(12., -0.5, 6.), vec3(.7, .8, 1.), 5., 0.1);
  lamps[2] = Lamp(vec3(-1.3, 0.8, -1.5), vec3(1., .95, .8), 3.5, 0.1);

  vec3 ray = GetCameraDir(fragCoord);

  RenderData traceinf = trace0(camera1.pos, ray, false);

  if (color_cyling)
    diamondColor = vec3(0.9)+0.1*vec3(sin(iTime*2.0),sin(iTime*1.412),sin(iTime));

  if (only_shape)
  	return vec4(traceinf.col, 1.0);

  vec3 col = traceinf.col;
  if (traceinf.objnr == objIsDiamond)
  {
  	vec3 norm = traceinf.norm;
    vec3 ray_r = refract(ray, traceinf.norm, 1./ior);
    vec3 ray_r2;

    int n2;
    for (int n=0; n<nb_refr; n++)
    {
      traceinf = trace0(traceinf.pos, ray_r, true);
      col += traceinf.col;
      col *= diamondColor;
      ray_r2 = refract(ray_r, traceinf.norm, ior);
      if (length(ray_r2) != 0.)
      {
      	col += sky_color(ray_r2)*diamondColor;
        break;
      }
      ray_r2 = reflect(ray_r, traceinf.norm);
      ray_r = ray_r2;
      n2 = n;
    }
    if (n2==nb_refr-1)
      if (show_unfinished)
        col = vec3(1., 0., 1.);
      else
    col += sky_color(ray_r2)*diamondColor;

    // Outer reflection
    float r = fresnel(ray, norm, ior);
    col = mix(col, sky_color(reflect(ray, norm)), r);
  }
  return vec4(col, 1.0);
}

//----------------------------------------------------------
// render color dispartment if defined
//----------------------------------------------------------
vec4 render_rgb(vec2 fragCoord)
{
  #ifdef color_disp
    return vec4 (render(fragCoord, ior_r).r
                ,render(fragCoord, ior_g).g
                ,render(fragCoord, ior_b).b, 1.0);
  #else
   	return render(fragCoord, ior);
  #endif
}
//----------------------------------------------------------
// render antialiased depending on 'aaSamples'
//----------------------------------------------------------
vec4 render_aa(vec2 fragCoord)
{
  // Antialiasing
  float aaScale = aaWidth / max(float(aaSamples-1), 1.);
  vec4 vs = vec4(0.0);
  for (int j=0; j<aaSamples; j++)
  {
    float oy = float(j) * aaScale;
    for (int i=0; i<aaSamples; i++)
    {
      float ox = float(i) * aaScale;
      vs += render_rgb(fragCoord + vec2(ox, oy));
    }
  }
  return vs / vec4(aaSamples*aaSamples);
}

//----------------------------------------------------------
void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
  map_prepare();
  fragColor = render(fragCoord, ior);
}
