#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec3 color = vec3(0.0);

    vec2 nfragCoord = fragCoord / iResolution.xy * 2.0 - 1.0;
    float aspct = (iResolution.x / iResolution.y);
    nfragCoord.x *= aspct;

    nfragCoord = nfragCoord * 1.0;

    float weight = 2.0;
    float reduce = 4.0;

    for (float i = 0.0; i < weight; ++i) {

        color.r += step(sin(iTime / 2.0) / 8.0, (nfragCoord.x * cos(iTime / 3.0) + nfragCoord.y * sin(iTime / (5.0 + 0.03 * i)))) / (weight * reduce);
        color.g += step(sin(iTime / 3.0) / 8.0, (nfragCoord.x * cos(iTime / 3.0) + nfragCoord.y * sin(iTime / (7.0 + 0.02 * i)))) / (weight * reduce);
        color.b += step(sin(iTime / 5.0) / 8.0, (nfragCoord.x * cos(iTime / 3.0) + nfragCoord.y * sin(iTime / (11.0 + 0.05 * i)))) / (weight * reduce);

    }



    fragColor = vec4(vec3(color), 1.0);

}
