#define R iResolution
#define T iTime/3.0
void mainImage( out vec4 O, in vec2 P )
{
    vec2 u = abs(2.0*P-R.xy)/R.x;
    float x=pow(u.y,u.x)*(0.5+sin(T)/2.0);
    float y=pow(u.x,u.y)*(0.5+cos(T)/2.0);
    float v = (round(fract(x*20.0))+round(fract(y*20.0)))/2.0;
    O = vec4(v,v,v, 1.0);
}
