void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 norm_mouse = iMouse.xy / iResolution.xy;
    if(uv.x >= norm_mouse.x){
        fragColor = vec4(texture(iChannel0, uv - 0.5 * (1.0 / iResolution.xy)).rgb,1.0);
    }else{
        fragColor = vec4(texture(iChannel1, uv).rgb,1.0);
    }

}

