const float pi = 3.14159265;
const float halfpi = 0.5*pi;
const float invpi = 1.0/pi;

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv.x = 2.0 * uv.x - 1.0; // map from [0,1] to [-1,+1]
    uv.x = uv.x * (iResolution.x / iResolution.y); // center x
    uv.x = 0.5 * (1.0 + uv.x); // map back from [-1,+1] to [0,1]

    const vec3 col1 = vec3(0.5, 0.0, 0.0);
    const vec3 col2 = vec3(0.0, 0.0, 0.5);
    const vec3 bg = vec3(0.8,0.6,0.2);
    const float inner = 0.25;
    const float outer = 0.4;
    // r in interval [0,1]
    float r = (1.0+sin(iTime*0.6))*0.5;

    float d = distance(vec2(0.5), uv);
    vec2 toc = normalize(uv-vec2(0.5));
    float angle = halfpi - 0.5*acos(toc.y);
    angle = angle * (toc.x < 0.0 ? -1.0 : 1.0);
    // map angle to [0,1]
    float scaled_angle = (halfpi+angle)*invpi;
    vec3 col;
    if (d<outer&&d>inner) {
        col = scaled_angle > r ? col1 : col2;
    } else {
        col = bg;
    }
    fragColor = vec4(col,1.0);
}
