float f_n_random(vec2 o){
    float n = (pow(257.89, o.x) + pow(83.367, o.y))*1217.;
    return fract(n+o.x*23.23 + o.y/o.x*0.3123);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 o_pix_coo_nor = (fragCoord.xy - iResolution.xy*0.5) /iResolution.y;
    vec2 o_mou_coo_nor = (iMouse.xy - iResolution.xy*0.5) /iResolution.y;
    float n_pix_size_max = floor(2. * iMouse.x);
    vec2 o_pix_coo_big = floor(o_pix_coo_nor*n_pix_size_max)/n_pix_size_max;
    fragColor = vec4(f_n_random(o_pix_coo_big));

}
