void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 u = fragCoord/iResolution.xy;
    vec4 a = texture(iChannel0,u);
    fragColor = (cos(length(a.xy)*-2.+vec4(0,1,2,4))*.5+.5)*a.z;
    //fragColor = a.zzzz;
    //fragColor = a;
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    vec4  r = vec4(0);
    vec4  a = A(u);
    float z = 6.;//kernel convolution size
    for(float i=-z; i<=z; ++i){
    for(float j=-z; j<=z; ++j){
        float l = length(vec2(i,j));
        if((i==0.&&j==0.)|| l>z-.1){continue;}
        vec2  c = normalize(vec2(i,j));
        vec4  a2= A(u+vec2(i,j));
        vec4  b = a2-a;
        //r.xy += c*b.z*cos(dot(b.xy,c.yx*vec2(-1,1))*8.)*exp(-l*l*.2)*.5;
        r.xy += c*min(b.z,0.)*cos(dot(b.xy,c.yx*vec2(-1,1))*16.)*exp(-l*l*.2)*.5;
    }}
    fragColor = r;
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
#define B(u) texture(iChannel1,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    vec4 t = A(u);
    vec2 m = +t.xy +B(u).xy;
    float s = 0.;
    float z = 6.;//kernel convolution size
    for(float i=-z; i<=z; ++i){
    for(float j=-z; j<=z; ++j){
        if(length(vec2(i,j))>z-.1){continue;}
        vec2 c = (m+vec2(i,j));
        s += exp(-dot(c,c));
    }}
    if(s==0.){s = 1.;}
    s = 1./s;

    fragColor = vec4(m,s,0);
}

#define A(u) texture(iChannel0,(u)/iResolution.xy)
#define B(u) texture(iChannel1,(u)/iResolution.xy)
void mainImage( out vec4 fragColor, in vec2 u )
{
    float tz = 0.;
    vec4  a  = vec4(0);
    float z  = 6.;//kernel convolution size
    for(float i=-z; i<=z; ++i){
    for(float j=-z; j<=z; ++j){
        if(length(vec2(i,j))>z-.1){continue;}
        vec4 t = A(u+vec2(i,j));
        vec4 m = B(u+vec2(i,j));
        vec2 c = (m.xy-vec2(i,j));
        float z = t.z*exp(-dot(c,c));
        a.xy += z*m.xy;
        a.z  += z*m.z;
        tz   += z;
    }}
    if(tz==0.){tz = 1.;}
    a.xy /= tz;
    //a = A(u);
    if(iMouse.z>0.)
    {
        vec2 m = 22.*(u-iMouse.xy)/450.;
        a += vec4(-normalize(m),0,0)*exp(-dot(m,m));
    }
    float keyW = texture( iChannel2, vec2(87.5/256.,.25) ).x;
    if(iFrame==0||keyW!=0.)
    {
        vec2 m = 22.*(u-iResolution.xy*.5)/450.;
        a = vec4(0,0,.4,0)-vec4(0,2,0,0)*exp(-dot(m,m));
    }
    fragColor = a;
}
