const float PI = 3.1415926535;

vec2 moveUv(vec2 _uv){
    float tension = abs(pow(sin(iTime), 2.));
    return vec2(
        _uv.x / 1.5 - (cos(iTime) * .125 - .125),
        (_uv.y + tension * sin(_uv.x * PI * 10.) / 50. ) / 1.5 + (cos(iTime) * .125 + .125)
    );
}

vec2 distort(vec2 _uv){
    return vec2(_uv.x / 1.5 + sin(_uv.y) * cos(_uv.x), _uv.y / 1.5 - cos(_uv.y) * sin(_uv.x));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    float _t = abs(sin(iTime) * .05);
    float threshold = -1. * cos(iTime);
    vec2 movedUv = moveUv(uv);
    vec4 tex_a = texture(iChannel0, mix(distort(uv), movedUv, smoothstep(threshold, threshold + _t, uv.x - uv.y))),
         tex_b = texture(iChannel1, mix(movedUv, distort(uv), smoothstep(threshold - _t,threshold,uv.x - uv.y)));


    // Output to screen
    fragColor = mix(tex_a, tex_b, step(uv.x - uv.y, threshold));
}
