#define u_resolution iResolution
#define u_time iTime
#define PI 3.14159265359

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec2 st = uv;
    vec2 WaveCentre = vec2(0.5, 0.5);
    float ratio = u_resolution.y/u_resolution.x;
    WaveCentre.y *= ratio; 
    //st.y *= ratio;    

    float downScale = 2.;
	float dist = distance(vec2(st.x,st.y*ratio), WaveCentre) * downScale;

    float val = dist;
    float a = 3.;

    // you can interpolate this value for animation curve
    float pulseSpeed = u_time*10.3;

    float cosFuns = cos(val*20.- u_time*10.3);
    float powFuns = pow(val*2.5,a);
    float limtedPowFuns = 0.5 * pow(a/(a + powFuns),2.);

    float waveStrength = 1.;

    float finalRes = smoothstep(0.,1.,limtedPowFuns * cosFuns) * waveStrength;

 	vec3 col = finalRes*vec3(1.);

    // Convert uv range to -1 to 1
    st = st * 2.0 - 1.0;
    // Scale uv
    st *= 1. + finalRes * 0.1;
    // Convert back to 0 to 1 range
    st = st * 0.5 + 0.5;

    vec4 texCol = texture(iChannel0,st);
    texCol += (texCol * finalRes) / ( dist * 10.0);

    // Output to screen
    vec4 rippleCol = vec4(col ,1.);
    fragColor = rippleCol;
    fragColor = texCol ;


}
