// ========================== Configuration  ==========================

// Comment to disable shader reduction
#define ENABLE

// Target resolution
// I'm unsure if this is respected as intended, it sets how pixelated the image is.
// Smaller means more pixelated. The image will be stretched to fill the viewport.
#define RES vec2(320., 200.)

// Score function
// The closer the color, the smaller the score
// The smallest score will be our chosen one
// It's worth playing around with, maybe you'll find a better function!
float score( in vec4 in1, in vec4 in2 ) {
    return distance(in1, in2);
}

// Color options
// These are the only colors we're allowed to use
// Feel free to play with different colors if you'd like!
const vec4[] COLORS = vec4[] (
      vec4(0., 0., 0., 1.), // Black
      vec4(124. / 256., 18. / 256., 140. / 256., 1.), // Pink
      vec4(92. / 256., 1. / 256., 32. / 256., 1.) // Red
);

// ========================== Main Shader Code ==========================

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Recalculate fragCoord according to our pizel size
    vec2 pSize = iResolution.xy / RES; // Size of a vPixel
    vec2 pos = floor(fragCoord / pSize); // Position in our vPixel grid

    fragCoord = (pos * pSize) + (pSize / 2.); // Find appropriate full-scale pixel for our vPixel

    // Take the color from iChannel0 (input image)
    fragColor = texture(iChannel0, fragCoord / iResolution.xy);

    #ifdef ENABLE
    // Find the closest color in terms of the color space
    int best_color = 0; // Color index
    float best_score = 999999999999999.; // Distance

    for (int i = 0; i < COLORS.length(); i++) {
        float score = score(fragColor, COLORS[i]);
        if (score < best_score) {
            best_color = i;
            best_score = score;
        }
    }

    fragColor = COLORS[best_color];
    #endif
}
