float luma(vec3 color) {
  return dot(color, vec3(0.299, 0.587, 0.114));
}

float luma(vec4 color) {
  return dot(color.rgb, vec3(0.299, 0.587, 0.114));
}

float dither2x2(vec2 position, float brightness) {
  int x = int(mod(position.x, 2.0));
  int y = int(mod(position.y, 2.0));
  int index = x + y * 2;
  float limit = 0.0;

  if (x < 8) {
    if (index == 0) limit = 0.25;
    if (index == 1) limit = 0.75;
    if (index == 2) limit = 1.00;
    if (index == 3) limit = 0.50;
  }

  return brightness < limit ? 0.0 : 1.0;
}

vec3 dither2x2v3(vec2 position, vec3 color) {
  return color * dither2x2(position, luma(color));
}

vec4 dither2x2(vec2 position, vec4 color) {
  return vec4(color.rgb * dither2x2(position, luma(color)), 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy / iResolution.xy;
    float mouse = (iMouse.x-iResolution.x) / (iMouse.y-iResolution.y*2.);
	vec2 pos = (uv.xy-0.5);
	vec2 cir = ((pos.xy*pos.xy+sin(uv.x*18.0+iTime-mouse)/25.0*sin(uv.y*7.0+iTime*1.5)/1.0)+uv.x*sin(iTime)/16.0+uv.y*sin(iTime*1.2)/16.0);
	float circles = (sqrt(abs(cir.x+cir.y*0.5)*25.0)*5.0);

    float time =iTime+1105.;
    uv = ((fragCoord * 2.0 - iResolution.xy) / iResolution.y) / 2.;
    float d = length(uv);
    d = pow(d,0.8);
    d =fract((time/6.5)+1192.*sqrt(d*d)*(d*(fract(tan(d)*0.07))))-(abs(uv.y*1.2))*length(uv);

	fragColor = vec4(0.,abs(sin(circles*1.0-1.0)-sin(circles))*d,abs(sin(circles)*1.0),1.0);
    fragColor = vec4(vec3(smoothstep(0.1,0.9,fragColor.g*fragColor.b)),1.0);
    fragColor = vec4(vec3(step(0.5,dither2x2(fragCoord.xy/vec2(2),fragColor))),1.0);
}
