// https://www.neilmendoza.com/glsl-rotation-about-an-arbitrary-axis/
mat3 twist3D(vec3 axis, float a) {          // ad
float s=sin(a);
float c=cos(a);
float oc=1.0-c;
vec3 as=axis*s;
mat3 p=mat3(axis.x*axis,axis.y*axis,axis.z*axis);
mat3 q=mat3(c,-as.z,as.y,as.z,c,-as.x,-as.y,as.x,c);
return p*oc+q;
}

