float sdCircle(vec2 uv,float r){
    return length(uv)-r;
}
float sdRect(vec2 uv,float r){
    return max(abs(uv.x),abs(uv.y))-r;
}
float smin(float a,float b,float k){
    float h = clamp(0.5+0.5*(a-b)/k,0.0,1.0);
    return mix(a,b,h)-k*h*(1.0-h);
}
float smax(float a,float b,float k){
    return -smin(-a,-b,k);
}
mat2 rot(float angle){
    float c= cos(angle);
    float s=  sin(angle);
    return mat2(c,-s,s,c);
}
float gouyu(vec2 p,float angle){
     p*=rot(angle);
     float w = 1.0/iResolution.y;
     float  c = sdCircle(p,0.2);
     float c2 = sdCircle(p-vec2(-0.1,0.0),0.103);
     float s = max(-c2,c);
     s = max(s,p.y);
     float c3 = sdCircle(p-vec2(0.102,0.0),0.099);
     s = min(s,c3);

     return smoothstep(w,-w,s);
}
float gouyu3(vec2 p){
    p*=1.8;
    float g = gouyu(p-vec2(0.00,0.4),radians(140.));
    g+=gouyu(p-vec2(1.732*.2,-0.4*0.5),radians(260.));
    g+=gouyu(p-vec2(-1.732*.2,-0.4*0.5),radians(0.));
    return g;
}
float Scene(vec2 uv){


    return gouyu3(uv);
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;
    vec3 col = vec3(0.0);

    col = mix(col,vec3(1.0,0.0,1.0),Scene(uv));

    fragColor = vec4(col,1.0);
}
