#define R iResolution.xy
#define A(U) texture(iChannel0,(U)/R)
#define B(U) texture(iChannel1,(U))
#define C(U) texture(iChannel2,(U)/R)
#define D(U) texture(iChannel3,(U)/R)
#define Main void mainImage (out vec4 Q, in vec2 U)
#define ei(a) mat2(cos(a),sin(a),-sin(a),cos(a))
vec3 hash(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

bool ignore_water;
float map (vec3 p, inout float s, inout vec4 Q) {

    float r = 1e9;
    Q = vec4(0);
    s = 0.;
    float h = p.y+.5*texture(iChannel2,.2*p.xz*600./R.x+0.5).x;

    { //tunnel
        vec3 pt = p;
        pt.x += .5*cos(pt.z);
        vec2 uv = vec2(pt.z-.5,atan(pt.y,pt.x))*.5/3.14159;
        vec4 c = B(uv);
        pt.xy += .03*pt.xy*c.x;
        float rt = 1.-length(pt.xy);
        if (rt < r) {
            r = rt;
            Q = c;
            s = .2;
            if (h < 0.) s = 1.;

        }
    }
    { // light
        vec3 pl = p;
        pl.x += .5*cos(pl.z);
        float rl = min(
            length(pl.xy-vec2(.1,.8))-.02,
            length(pl.xy-vec2(-.1,.8))-.02
        );
        if (rl < r) {
            r = rl;
            Q = vec4(6)+5.*sin(vec4(1,2,3,4)+pl.z);
            s = 1.;
        }
    }
    if (!ignore_water) { // water
        h = abs(h)-.03;
        if (h < r) {
            r = h;
            Q = vec4(.4,.8,1,1);
            s = 0.;
        }
    }

    return r;
}
vec3 normal (vec3 p) {
    vec2 e = vec2(1e-2,0);
    float s = 0.; vec4 q = vec4(0);
    return normalize(vec3(
        map(p+e.xyy,s,q)-map(p-e.xyy,s,q),
        map(p+e.yxy,s,q)-map(p-e.yxy,s,q),
        map(p+e.yyx,s,q)-map(p-e.yyx,s,q)
    ));
}
void scene (inout vec3 p, inout vec3 d, inout vec4 Q, float b) {
    float r = 0.,s = 0.; vec3 n = vec3(0,0,1);
    vec3 p0 = p; vec4 q = Q;
    for (float i = 0.; i < 200.; i++)
    {
        r = map(p,s,q);
        p += d*r;
    }
    n = normal(p);
    if (s > 0.) {
        d = reflect(d,n);
        vec3 h = hash(vec2(b,iFrame));
        h.xy = s*h.x*vec2(cos(6.28318530718*h.y),sin(6.28318530718*h.y));

        vec3 u = cross(d,vec3(1,0,0));
        if (length(u)<.9) u = cross(d,vec3(0,1,0));
        vec3 v = cross(d,u);

        d = normalize(d+u*h.x+v*h.y);

        Q *= q;
    } else {
        if (hash(vec2(3.+b,iTime)).x<.5) {
            d = reflect(d,n);
            Q *= q;
        } else 
        { 
            d = refract(d,n,.5);
        }
    }
    if (s == 0.) ignore_water = true;
    else ignore_water = false;
    p += 1e-3*d;
}


Main {
    vec2 u = 2.*(U-.5*R)/R.y;
    vec2 h = hash(vec2(iFrame,iTime)).xy;
    h = sqrt(h.x)*vec2(cos(6.28318530718*h.y),sin(6.28318530718*h.y));
    vec3 p = vec3(.4*u-.0*h-vec2(.1,0.),0);
    vec3 d = normalize(vec3(u+.01*h,2));
    Q = vec4(1);
    ignore_water = false;
    scene(p,d,Q,0.);
    scene(p,d,Q,1.);
    scene(p,d,Q,2.);
    scene(p,d,Q,3.);
    Q += A(U);
}
