/**/
void mainImage( out vec4 O, in vec2 SC )
{
    float speed = 100.;
    vec2 R = iResolution.xy * floor(mod(iTime * speed, 7000.));
         SC = floor(SC);
    float f_index =   SC.y  *     R.x  +     SC.x;
    int i_index = int(SC.y) * int(R.x) + int(SC.x);

    O = vec4( i_index != int(f_index) );
            //i_index != int(float(i_index)) // Works on CPU, but not on GPU.
}
