#define PI 3.14159265

vec3  rot(vec3 p,vec3 a,float t)
{
	a=normalize(a);
	return mix(a*dot(p,a),p,cos(t))+sin(t)*cross(p,a);
}

float map(vec3 p)
{
	p=rot(p,vec3(cos(iTime*.3),sin(iTime*.5),.5*sin(iTime*.2)),iTime*.7);
    p.zx=vec2(atan(p.z,p.x)/PI*4., length(p.zx)-1.5);
    p.xy=vec2(atan(p.x,p.y)/PI*.7, length(p.xy)-.3);
    p.x+=p.z;
    p.x=mod(p.x,.2)-.1;
    return length(p.xy)-.07;
}

vec3 calcNormal(vec3 p)
{
    vec3 e = .001*vec3(-1,1,1);
    return normalize ( e*map(p+e) + e.yxy*map(p+e.yxy) + e.yyx*map(p+e.yyx) - e.xxx*map(p-e.xxx) );
}

