#ifdef GL_ES
precision mediump float;
#endif

#define TWO_PI 6.28318530718


//  Function from IÃ±igo Quiles
//  https://www.shadertoy.com/view/MsS3Wc
vec3 hsb2rgb( in vec3 c ){
    vec3 rgb = clamp(abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),
                             6.0)-3.0)-1.0,
                     0.0,
                     1.0 );
    rgb = rgb*rgb*(3.0-2.0*rgb);
    return c.z * mix( vec3(1.0), rgb, c.y);
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    //squash screen space into a square instead of a rectangle
    vec2 uv = fragCoord/iResolution.xy;
    float screenRatio = iResolution.x/iResolution.y;
    uv.x *= screenRatio;
    uv.x -= .4;
    vec3 color = vec3(0.0);

    // Use polar coordinates instead of cartesian
    vec2 toCenter = vec2(0.5)-uv;
    float angle = atan(toCenter.y,toCenter.x);
    float radius = length(toCenter)*2.0;


    //E1: MAKE COLOR WHEEL SPIN
    //redundant but i just needed to make this its own line
    float time = iTime;
    color = hsb2rgb(vec3((angle/TWO_PI)+time,radius,1.0));


    //E2: MAKE ONE HUE COVER A GREATER ANGLE AND ALL THE REST A SMALLER ANGLE
    //distort the angle -- color bands will look narrow on the 'ends', wide in the 'center'
    angle = (pow(angle, 3.0));
    color = hsb2rgb(vec3((angle/TWO_PI)+time,radius,1.0));


    //E3: FIX THE COLOR WHEEL WITH SHAPING FUNCTIONS
    //ref to match: https://thebookofshaders.com/06/colorwheel.png
    //angle = atan(toCenter.y,toCenter.x);
    //angle = angle/TWO_PI + .5;
    //angle = pow(angle, 2.0);
    //color = hsb2rgb(vec3((angle + 0.05),radius,1.0));


    //E4: add the circle frame around the wheel
    vec2 center = vec2(0.5, 0.5);
    float len = distance(uv, center);
    len = step(.5, len);
    vec3 circle = vec3(len, len, len);
    circle = 1.0 - circle;


    fragColor = vec4(color * circle,1.0);
}
