void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_pix_coo_nor_strech = (fragCoord.xy ) / iResolution.xy;
    vec2 o_pix_coo_nor = (fragCoord.xy - iResolution.xy*0.5) / iResolution.y;


    vec4 o_sound_data = texture(
        iChannel0, 
        vec2(o_pix_coo_nor_strech.x, floor(o_pix_coo_nor_strech.y*10.0)/10.0)
    );
    float n_sample = o_sound_data.x;

    fragColor = vec4((n_sample));

}
