float hash(float num1, float num2) {
    return abs(fract(72.53 * sin((num1/9711.0 + 7.0) * 315.0 + 127.0) * cos((num2/3917.0+11.0) * 513.0) * 7759.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec3 col;
    col.r=hash(uv.x,uv.y+iTime);
    col.g=hash(uv.y+iTime,uv.x);
    col.b=hash(uv.x/2.0,uv.x+uv.y+iTime/2.0);
    fragColor = vec4(col,1.0);
}
