float spiralSDF(vec2 p,vec2 c, float f){
    // from https://www.shadertoy.com/view/tslcW4
    const float a=1.0;
    const float b=.1759;
    const float PI=3.14159265359;

    // t = theta
    p = p - c;
    float t=atan(p.y, p.x) + f;
    // t=(t+PI)/(2.*PI);
    float r=length(p.xy);

    float n=(log(r/a)/b-t)/(2.*PI);

    // Cap the spiral
    // float nm = (log(0.11)/b-t)/(2.0*PI);
    // n = min(n,nm);
    // return (n+1.0)/100.0;
    float upper_r=a*exp(b*(t+2.*PI*ceil(n)));
    float lower_r=a*exp(b*(t+2.*PI*floor(n)));
    // float lower_r = 0.0;

    return min(abs(upper_r-r),abs(r-lower_r));
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord*2.0 - iResolution.xy) / iResolution.y;

    float d = spiralSDF(uv, vec2(0.0, 0.0), iTime*8.0);
    float d2 = spiralSDF(uv, vec2(0.0, 0.0), iTime*4.0);
    float d3 = spiralSDF(uv, vec2(0.0, 0.0), iTime*16.0);

    d = smoothstep(0.0, 0.2, d)*0.9;
    d2 = smoothstep(0.0, 0.9, d2)*0.9;
    d3 = smoothstep(0.0, 0.5, d3)*0.9;

    // Output to screen
    fragColor = vec4(d, d2, d3,1.0);
}
