#ifdef GL_ES
precision mediump float;
#endif

float random (vec2 st) {
    return fract(sin(dot(st.xy,
                         vec2(12.9898,78.233)))*
        43758.5453123);
}

float random_fl(float x)
{
	return fract(sin(x)*1000000.0);    
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 st = fragCoord.xy/iResolution.xy;
    st.x *= iResolution.x/iResolution.y;
  	st.x *= 54.0;
    st.y *= 80.0;
    st.y += 1405.123456;

    // st = (st-vec2(5.0))*(abs(sin(u_time*0.2))*5.);
    st.x -= iTime * 60.0 * ((random_fl(floor(st.y)+0.15) * 2.0));

    vec2 ipos = floor(st);  // integer
    vec2 fpos = fract(st);  // fraction

    float thr = 0.5;
	thr = iMouse.x/iResolution.x;
    thr += random_fl(ipos.y) * 1.5 - .75;
    vec3 color = vec3(1.0-step(thr, random(ipos)));



    fragColor = vec4(color,1.0);
}
