float random(in vec2 st)
{
    return fract(sin(dot(st.xy ,vec2(12.9898,78.233))) * 43758.5453);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{   
    float scaleY = 2.;
    float scrollSpeed = .25;
    float sizeX = 77.;

    vec2 uv = vec2( fragCoord.x / iResolution.x , fragCoord.y / iResolution.y );

    float x = floor( uv.x * sizeX ) / sizeX;

    float rand = random( vec2( x , 0 ) );

    float y = uv.y + rand;

    #if 1 
    float rand_time = ( 1. + fract( rand * 77. ) );
    #else 
    float rand_time = 1.; // remove random scroll speed per strip 
    #endif

    y = fract( y + iTime * scrollSpeed * rand_time );
    y = 1. - y ;


    #if 1 
    y = clamp( y - (1.-1./scaleY) , 0. , 1. ) * scaleY;
    #else 
    //y = y + 0.;
    y = abs( ( y - (1.-1./ 2. ) ) * 2. ); // seamlessly connected vertical lines
    #endif


    fragColor = vec4(1,1,1,1) * y;

}
