void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    uv *= 10.+sin(iTime);
    vec2 gv = fract(uv)-.5;
    float t = iTime *.5;
    vec3 col = vec3(0.);
    float c1 = clamp(sin(t), .2,.8);
    vec4 colA = vec4(c1, cos(t), c1*.2,1.);
    vec2 id = uv-gv;
    id *= colA.xy;
    float d = length(gv);
    d *= clamp(sin(t+id.x*id.y), .5, 1.);
    float circ = smoothstep(.4,.2,d);

    col.rg = id;
    col += circ;

    fragColor = vec4(col,1.0);
}
