const mat4 dither = mat4(
    -4.0, 0.0, -3.0, 1.0,
    2.0, -2.0, 3.0, -1.0,
    -3.0, 1.0, -4.0, 0.0,
    3.0, -1.0, 2.0, -2.0
);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = fragCoord.xy/iResolution.xy;

    vec3 c = texture(iChannel0, uv).xyz;

    c*= vec3(.4,.5,.9);
    c += dither[int(fragCoord.x)%4][int(fragCoord.y)%4]*0.005;
    fragColor = vec4(c,1.0);
}

#define PI 3.14159265359

vec2 heart(float t)
{
    float s = sin(t);
    vec2 h = vec2(
        16.*s*s*s,
        15.*cos(t)-5.*cos(2.*t)-2.*cos(3.*t)-cos(4.*t)
    );
    h /= 14.;
    h.y += .2;
    return h;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord.xy-iResolution.xy)/iResolution.y;
    vec2 tuv = fragCoord.xy/iResolution.xy;

    float t = 1.1*iTime;
    vec2 h = heart(t);

    vec3 c = vec3(.2/length(h-vec2(-1.3,1.3)*uv));
    c = max(c, vec3(.2/length(h-vec2(1.3,1.3)*uv)));
    vec3 fb = texture(iChannel0, tuv).xyz;
    c = mix(c,fb,0.92);

    fragColor = vec4(c,1.0);
}
