void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.y;
    uv *= 2.;
    uv -= vec2(2.5, 0.5);
    uv = clamp(uv, 0., 1.);

    float frame = floor( mod( iTime*10.0, 6.0 ) );
	float nyanWidthUV = 40.0/256.0;
	uv.x = (uv.x + frame) * nyanWidthUV;

    vec4 nyan = texture( iChannel0, uv );

    vec2 p = fragCoord.xy / iResolution.xy;
    vec2 uv1 = vec2( p.x + mod(iTime,2.0), p.y );

    float h = (0.01 * sin(40.0 * p.x + 40.0 * iTime * 0.25) + p.y - 0.39) / 0.25;
    vec3 bg = vec3(0.0, 0.25, 0.45);

    if( p.x < 0.78 && h > 0.0 && h < 1.2 )
	{
	    h = floor( h*6.0 );
	    bg = mix( bg, vec3(1.0,0.0,0.0), 1.0 - smoothstep( 0.0, 0.1, abs(h-5.0) ) );
	    bg = mix( bg, vec3(1.0,0.5,0.0), 1.0 - smoothstep( 0.0, 0.1, abs(h-4.0) ) );
	    bg = mix( bg, vec3(1.0,1.0,0.0), 1.0 - smoothstep( 0.0, 0.1, abs(h-3.0) ) );
	    bg = mix( bg, vec3(0.0,1.0,0.0), 1.0 - smoothstep( 0.0, 0.1, abs(h-2.0) ) );
	    bg = mix( bg, vec3(0.0,0.5,1.0), 1.0 - smoothstep( 0.0, 0.1, abs(h-1.0) ) );
	    bg = mix( bg, vec3(0.5,0.0,1.0), 1.0 - smoothstep( 0.0, 0.1, abs(h-0.0) ) );
	}

    vec3 col = mix(bg, nyan.xyz, nyan.w);
    fragColor = vec4(col, 1.0);
}
