//Shane's original: https://shadertoy.com/view/cdycRt

//-11 Thanks to FabriceNeyret2
//-3 Thanks to coyote
void mainImage( out vec4 O, vec2 I )
{
    I -= O.xy = iResolution.xy * .5;
    I /= O.y;
    float W = 24.,
          a = atan(-I.x, I.y+=.5),
          v = length(I) * W,
          e = min(ceil(v), W);

    O = abs( cos( v/W/.4 + vec4(1,2,3,0) ) ) 
      * min((.4 - length(
                    vec2(  e*(clamp(a, 1.57*sin(iTime/.5+e/W*4.7), 1.57) 
                              - a )
                         , v-e+.5)
                        )
            )*O.y/35.
            ,1.);
}
