void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;


    float zoom = iTime * 10.;
    vec2 uvF = floor(fragCoord / zoom)/(iResolution.xy);


    vec4 col = vec4(hash3(uvF), hash3(uvF.yx), hash3(vec2(hash3(uvF), hash3(uvF.yx))), hash3(vec2(hash3(uvF.yx), hash3(uvF))));


    fragColor = col;
}

float hash3(vec2 xy){
    xy = mod(xy, .19);
    float h = dot(xy.yyx, vec3(.013, 27.15, 2027.3));
    h *= h;
    h *= fract(h);

    return fract(h);
}
