#define r(a)   mat2(cos(a+vec4(0,11,33,0)))          //

void mainImage( out vec4 C, vec2 U )
{
    float o=4.,i=0.,j=i,s,g=1.,k=.04;
    vec3 p,n,c,r= iResolution;
    p = 3.*vec3( g* ( U - r.xy/2. ) / r.y, g-2. );
    for(g = length(p)-2.1; i++ < 80.; g += o*k )
      { s = 1.;
        if(abs(o)<.005)continue; 
        n=r-r,
        p = 3.*vec3( g* ( U - r.xy/2. ) / r.y, g-2. );
        p.y+=2.;
        p.zy*=r(.5);
        p.xz *= r(.1*iTime+3.5);
        k *= 1.036;
        for(j=0.;j<12.;j++)p.y-=.15,o = min(o, length(p)-.16);
        for(;s<3.; s/=.9 ){
            p.xz *= r(1.),
            p.yz *= r(-.1),
            p.z = abs(p.z),          
            n = .06*sin(p+1.2).yzx*s,
            p = (p*s).yxz;
            if(s>1.5)p.x = abs(p.x); 
            for(j=0.;j<6.;j++)p = p-n,o = min(o, length(p)-.1);
            }                        
        //o = min(o, length(p*vec3(1,.2,.5))-.2);
        }        
    c = log(1. + p*o);    
    C = vec4(c,1);	
}
