// Texturing
// Eric Galin

const int Steps = 200;      // Number of steps
const float Epsilon = 0.01; // Marching epsilon

// Structure for objects
// v : Field value
// i : Texture index
struct V {
    float v; 
    int i;
};

// Structure for texture
// c : Color
// s : Specular
struct T {
vec3 c;
float s;
};

// Primitive functions -----------------------------------------------------------------------------------

// Sphere 
// p : point
// c : center 
// r : radius
V Sphere(vec3 p, vec3 c,float r,int index)
{
  return V(length(p-c)-r,index);
}

// Cube 
// p : point
// c : center 
// r : radius
V Cube(vec3 p,vec3 c,float r,int index)
{
   vec3 q = abs(p-c) - vec3(r);
  return V(length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0),index);
}

// Plane 
// p : point
// n : Normal of plane
// o : Point on plane
V Plane(vec3 p, vec3 n, vec3 o,int index)
{
    return V(dot((p-o),n),index);
}

// Union
// a : field function of left sub-tree
// b : field function of right sub-tree
V Union(V a,V b)
{
    if (a.v<b.v)
        return V(a.v,a.i);
    else 
    {
        return V(b.v,b.i);
    }
}

// Intersection, preserve the index of first object
// a, b : field function of left and right sub-trees
V Intersection(V a,V b)
{
    if (a.v>b.v)
    {
        return V(a.v,a.i);
    }
    else 
    {
        return V(b.v,a.i);
    }
}

// Dice 
// p : point
// c : center 
// r : radius
V Dice(vec3 p,vec3 c,float r,int index)
{
  return Intersection(Cube(p,c,r*0.75,index),Sphere(p,c,r,index));
}

// Potential field of the object
// p : point
V object(vec3 p)
{
  V u = Plane(p,normalize(vec3(0.0,0.0,1.0)),vec3(0.0,0.0,-4.0),0);

  u=Union(u,Dice(p,vec3( 0.0, 0.0, 2.0),4.0,1));
  u=Union(u,Dice(p,vec3( -10.0, 0.0, 2.0),4.0,2));
  u=Union(u,Dice(p,vec3( 10.0, 0.0, 2.0),4.0,3));
  u=Union(u,Dice(p,vec3( 20.0, 0.0, 2.0),4.0,4));
  u=Union(u,Dice(p,vec3( -20.0, 0.0, 2.0),4.0,5));
  u=Union(u,Dice(p,vec3( 0.0, -10.0, 2.0),4.0,6));
  u=Union(u,Dice(p,vec3( 0.0, 10.0, 2.0),4.0,7));
  u=Union(u,Dice(p,vec3( 0.0, 20.0, 2.0),4.0,8));
  u=Union(u,Dice(p,vec3( 0.0, -20.0, 2.0),4.0,9));





  return u;
}

// Analysis of the scalar field --------------------------------------------------------------------------

// Calculate object normal
// p : point
vec3 ObjectNormal(in vec3 p )
{
  float eps = 0.001;
  vec3 n;
  V vp = object(p);
    float v = vp.v;
  n.x = object( vec3(p.x+eps, p.y, p.z) ).v - v;
  n.y = object( vec3(p.x, p.y+eps, p.z) ).v - v;
  n.z = object( vec3(p.x, p.y, p.z+eps) ).v - v;
  return normalize(n);
}

// Trace ray using ray marching
// o : ray origin
// u : ray direction
// e : Maximum distance 
// h : hit
// s : Number of steps
float SphereTrace(vec3 o, vec3 u, float e,out bool h,out int s)
{
  h = false;

    // Start at the origin
    float t=0.0;

  for(int i=0; i<Steps; i++)
  {
    s=i;
    vec3 p = o+t*u;
    float v = object(p).v;
    // Hit object
      if (v < 0.0)
      {
          s=i;
          h = true;
          break;
      }
      // Move along ray
      t += max(Epsilon,v);
      // Escape marched too far away
      if (t>e)
      {
          break;
      }
  }
  return t;
}

// Background color
// d : Ray direction
vec3 background(vec3 d)
{
  return mix(vec3(0.65,0.75,0.995),vec3(0.652,0.697,0.995), d.z*0.5+0.5);
}


// Direct lighting
float Light(vec3 p,vec3 n)
{
   // point light
  const vec3 lp = vec3(5.0, 10.0, 25.0);

  vec3 l = normalize(lp - p);

  // Phong shading
  float diff = clamp(dot(n, l),0.0,1.0);

    bool h;
    int s;
    float t=SphereTrace(p+0.1*n,l,100.0,h,s);
if (!h)
    {
     return diff;
    }
    return 0.0; 
}
// Textures ----------------------------------------------------------------------------

int Checker(float x, float y, float z){

   return(int(abs(x)) + int(abs(y)) + int(abs(z))) % 2 ;

}

vec3 checkerColor(float x, float y, float z, vec3 color1, vec3 color2, float scale)
{
    // Escala las coordenadas para que los cuadros sean del tamaño deseado
    x *= scale;
    y *= scale;
    z *= scale;

    // Calcula el valor checker en la posición 3D
    int checkerVal = Checker(x, y, z);

    // Establece el color en función del valor checker
    vec3 checkerColor = checkerVal == 0 ? color1 : color2;

    return checkerColor;
}



float Axial(float x, float y, float z) {

    float r = sqrt ( x * x + y * y + z * z );
    float width = 2.; // Ancho de la línea
    float falloff = 2.0; // Factor de atenuación
    return 0.5 + 0.5 * cos (3.1415927 * pow(r, falloff) / width);
}


float WoodNoise(vec3 p) {

    return (Noise(p) + Noise(p * 2.0) * 0.5 + Noise(p * 4.0) * 0.25) / 1.75;
}


// Shading and lighting ---------------------------------------------------------------------------

T Texture0(in vec3 p)
{
  return T(vec3(0.80,0.80,0.80),0.45);   
}

T Texture1(in vec3 p)
{
  return T(vec3(0.82,0.81,0.90),0.45);   
}

T Texture2(in vec3 p)
{
  return T(vec3(0.82,0.94,0.80),0.60);   
}

T Texture3(in vec3 p)
{
  return T(vec3(0.82,0.91,0.90),0.0);   
}

T Checker_Color(in vec3 p, vec3 color1, vec3 color2, float scale)
{
  vec3 color;
  vec3 scale_p = p * scale;
  int checker = Checker(scale_p.x, scale_p.y, scale_p.z);
  if (checker == 0) {
    color = color1; // Primer color para los cuadrados negros
  } else {
    color = color2; // Segundo color para los cuadrados blancos
  }
  return T(color,1.0); // Devolver la estructura T con el color y la opacidad
}

T TextureCheckerConvencional(in vec3 p)
{
  return T(vec3(Checker(p.x,p.y,p.z)),1.0);
}

T Uniform(in vec3 p, vec3 color)
{
  return T(normalize(color),0.0);
}

T Axial_t(in vec3 p)
{
  return T(vec3(Axial(p.x,p.y,p.z)), 0.0);
}

T Axial_Color_Center(in vec3 p, vec3 color, vec3 center)
{

  vec3 q = p - center;
  return T(color * vec3(Axial(q.x,q.y,q.z)), 0.0);
}

T Noise_t(in vec3 p)
{
  float noise = Noise(p);
  float noise1 = Noise(p);
  return T(vec3(0., noise, 1.0), 0.3);
}

T Marbre(in vec3 p, float scale, float frequency, vec3 color1, vec3 color2, float sino)
{
  // Generar el ruido 
  float noise = Noise(p * scale);
  // Crear el patrón sinusoidal
  float pattern = sin(dot(p * frequency, vec3(1.0)));
  // Crear el mármol combinando el ruido y el patrón sinusoidal
  float marble = mix(noise, pattern, sino);
  // Asignar los colores de la textura de mármol
  vec3 color = mix(color1, color2, marble);
  // Crear la textura
  return T(color, 1.0);
}


T Marbre_ssv(in vec3 p, float scale, float frequency, vec3 color1, vec3 color2)
{
  // Generar el ruido
  float noise = Noise(p * scale);
  // Crear la textura de mármol sin venas
  vec3 color = mix(color1, color2, noise);
  // Crear la textura
  return T(color, 1.0);
}

T Wood_t(in vec3 p, vec3 color1, vec3 color2) {

    float noise = WoodNoise(p);
    vec3 color = mix(color1, color2, pow(noise, 1.0));
    return T(color, 0.6);
}

T Voronoi_t(in vec3 p, vec3 algo, vec3 color, float alpha) {

    float scale = 0.4; // valor arbitrario para la intensidad del patrón Voronoi
    vec3 v = Voronoi(p);
    vec3 col = mix(vec3(1.0), color, clamp(scale * v.x, 0.0, 1.0));
    return T(col, alpha);
}

// Compute texture 
// p : Point
// n : Normal
T Color(in vec3 p,in vec3 n)
{
    V vp= object(p);
    if (vp.i==0){

        return Marbre_ssv(p, 1.0, 1.0, vec3(0.9,0.8,0.1), vec3(0.5,0.4,0.1));
    }
    else if (vp.i==1)
    {
        return Marbre(p, 1.0, 1.0, vec3(0.8), vec3(1.0), 0.4);
    }
    else if (vp.i==2)
    {
        return Wood_t(p, vec3(0.6,0.4,0.2), vec3(0.9,0.6,0.4));
    }
    else if (vp.i==3)
    {
        return Voronoi_t(p, vec3(0.,0.,0.),vec3(355.,0.,0.),0.);
    }
    else if (vp.i==4)
    {
        return Checker_Color(p, vec3(1.,1.,0.), vec3(0.,0.,0.), 0.8);
    }
    else if (vp.i==5)
    {
        return TextureCheckerConvencional(p);
    }
    else if (vp.i==6)
    {
        return Uniform(p, vec3(1.,0.,1.));
    }
    else if (vp.i==7)
    {
        return Axial_Color_Center(p, vec3(1.,0.,0.), vec3(0.,10.,2.));
    }

    else if (vp.i==8)
    {
        return Axial_Color_Center(p, vec3(0.,1.,0.), vec3(0.,0.,0.));
    }

    else if (vp.i==9)
    {
        return Noise_t(p);
    }
}

// Shadowing
// p : Point
// n : Normal
// l : Light direction
float Shadow(vec3 p,vec3 n,vec3 l)
{
    bool h;
    int s;
    float t = SphereTrace(p,l,100.0,h,s);
    if (!h)
    {
     return 1.0;
    }
    return 0.0; 
}

// Shading and lighting
// p : Point
// n : Normal at point
// e : Eye direction
vec3 Shade(vec3 p, vec3 n, vec3 e)
{
     // Point light
    const vec3 lp = vec3(5.0, 10.0, 25.0);

    // Light direction to point light
    vec3 l = normalize(lp - p);

    T tex=Color(p,n);

    // Ambient color
    vec3 ambient = 0.5*tex.c;

    // Shadow computation
    float s = Shadow(p+0.01*n,n,l);

// Phong diffuse
    vec3 diffuse = 0.5  * clamp(dot(n, l),0.0,1.0) * tex.c;

    // Specular
    vec3 r = reflect(e,n);
    vec3 specular = tex.s * pow(clamp(dot(r,l),0.0,1.0),28.0) * vec3(1.0,1.0,1.0);
    vec3 c = ambient + s * (diffuse + specular);
    return c;
}

// Picture in picture ------------------------------------------------------------------------------

// Shading according to the number of steps in sphere tracing
// n : Number of steps
vec3 ShadeSteps(int n)
{
   float t=float(n)/(float(Steps-1));
   return 0.5+mix(vec3(0.05,0.05,0.5),vec3(0.65,0.39,0.65),t);
}

// Picture in picture
// pixel : Pixel
// pip : Boolean, true if pixel was in sub-picture zone
vec2 Pip(in vec2 pixel, out bool pip)
{
    // Pixel coordinates
    vec2 p = (-iResolution.xy + 2.0*pixel)/iResolution.y;
   if (pip==true)
   {    
    const float fraction=1.0/4.0;
    // Recompute pixel coordinates in sub-picture
    if ((pixel.x<iResolution.x*fraction) && (pixel.y<iResolution.y*fraction))
    {
        p=(-iResolution.xy*fraction + 2.0*pixel)/(iResolution.y*fraction);
        pip=true;
    }
       else
       {
           pip=false;
       }
   }
   return p;
}


// Image
void mainImage( out vec4 color, in vec2 pxy )
{
    // Picture in picture on
    bool pip=true;

   // Pixel
     vec2 pixel=Pip(pxy, pip);

    // Mouse
    vec2 m=iMouse.xy/iResolution.xy;

    // Camera
    vec3 ro,rd;
    Ray(m,pixel,ro,rd);

  // Trace ray

  // Hit and number of steps
  bool hit;
  int s;

  float t = SphereTrace(ro, rd, 100.0,hit, s);

    // Position 
    vec3 pt = ro + t * rd;

  // Shade background
  vec3 rgb = background(rd);

  if (hit)
  {
    // Compute normal
    vec3 n = ObjectNormal(pt);

    // Shade object with light
    rgb = Shade(pt, n, rd);
  }

  // Uncomment this line to shade image with false colors representing the number of steps
  if (pip==true)
  {
      rgb = ShadeSteps(s); 
  }


  color=vec4(rgb, 1.0);
}

// Texturing and noise 

// Hashing function
// Returns a random number in [-1,1]
// p : Vector in space
float Hash(in vec3 p)  
{
    p  = fract( p*0.3199+0.152 );
	p *= 17.0;
    return fract( p.x*p.y*p.z*(p.x+p.y+p.z) );
}

// Procedural value noise with cubic interpolation
// x : Point 
float Noise(in vec3 p)
{
    vec3 i = floor(p);
    vec3 f = fract(p);

    //f = f*f*(3.0-2.0*f);
    // Could use quintic interpolation instead of cubic
    f = f*f*f*(f*(f*6.0-15.0)+10.0);

    return mix(mix(mix( Hash(i+vec3(0,0,0)), 
                        Hash(i+vec3(1,0,0)),f.x),
                   mix( Hash(i+vec3(0,1,0)), 
                        Hash(i+vec3(1,1,0)),f.x),f.y),
               mix(mix( Hash(i+vec3(0,0,1)), 
                        Hash(i+vec3(1,0,1)),f.x),
                   mix( Hash(i+vec3(0,1,1)), 
                        Hash(i+vec3(1,1,1)),f.x),f.y),f.z);
}

// Compute the distance to the Voronoi boundary
// x : Point
// Return (closest distance, second closest, cell id)
vec3 Voronoi( in vec3 x )
{
    vec3 p = floor( x );
    vec3 f = fract( x );

	float id = 0.0;
    vec2 res = vec2( 100.0 );
    for( int k=-1; k<=1; k++ )
    for( int j=-1; j<=1; j++ )
    for( int i=-1; i<=1; i++ )
    {
        vec3 b = vec3( float(i), float(j), float(k) );
        vec3 r = vec3( b ) - f + Hash( p + b );
        float d = dot( r, r );

        if( d < res.x )
        {
			id = dot( p+b, vec3(1.0,57.0,113.0 ) );
            res = vec2( d, res.x );			
        }
        else if( d < res.y )
        {
            res.y = d;
        }
    }

    return vec3( sqrt( res ), abs(id) );
}

// Camera -----------------------------------------------------------------------

// Rotation matrix around z axis
// a : Angle
mat3 Rz(float a)
{
    float sa = sin(a); float ca = cos(a); 
    return mat3(ca,sa,0.0,    -sa,ca,0.0,  0.0,0.0,1.0);
}

// Compute the ray
//      m : Mouse position
//      p : Pixel
// ro, rd : Ray origin and direction
void Ray(in vec2 m, in vec2 p,out vec3 ro,out vec3 rd)
{
    float a = 3.0*3.14*m.x;
   	float le = 3.8;

    ro=vec3(60.0,0.0,35.0);
    ro*=Rz(3.0*3.14*m.x); 

    vec3 ta = vec3(0.0,0.0,0.0);
    vec3 ww = normalize( ta - ro );
    vec3 uu = normalize( cross(ww,vec3(0.0,0.0,1.0) ) );
    vec3 vv = normalize( cross(uu,ww));
	rd = normalize( p.x*uu + p.y*vv + le*ww );
}
