#define rot(r) mat2(cos(r), -sin(r), sin(r), cos(r))
#define T iTime

void mainImage(out vec4 o, vec2 u) {    
    vec3 p = iResolution; 
    u = (u - p.xy/2.) / p.y;  

    o -= o;
    float i, d, s, acc;

    while(i++ < 100.){
        p = vec3(u * acc * .15, 5. - acc);
        p.zx *= rot(T);

        p *= d = 30. / dot(p, p);

        p.zy *= rot(p.x * .1 + T * 8.);

        s = sin(p.y / 100.) * 10.  +  4.; 

        d = s/d;

        o += .01 / exp(300. * d);

        acc += d;
    }
}
