   vec3 palette( float t ){
    vec3 a = vec3(0.697, 0.019, 0.395);
    vec3 b = vec3(0.785, 0.704, 0.164);
    vec3 c = vec3(0.5,0.969,1.000);
    vec3 d = vec3(0.122,0.357,0.204);




   return a +b*cos( 6.28319*(c*t*d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 uv0 = uv;
    vec3 finalColor = vec3(0.0);

 for (float i = 0.1; i < 4.0; i++) {   

    uv = fract (uv + 1.0)- 0.5;

    float d = length(uv);
    vec3 col = palette(length(uv0) + iTime)/2.;

    d = sin(d*8. - iTime)/15.;

    d = 0.05 / d;

    finalColor += col * d;
 }   
    fragColor = vec4(finalColor, 1.0);



}
