float createWave(float amp, float x, float freq, float speed) {
    return (amp*sin(x * freq + iTime * speed));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec3 col = vec3(0,0,0);

    float a = 100.0;
    float f = 50.0;
    float s = 10.0;

    // Waves
    float wave = createWave(a, uv.x, f, s);
    wave += createWave(100.0, uv.x, 10.0, 1.0);
    wave += createWave(100.0, uv.x, 10.0, 2.0);
    //wave += createWave(100.0, uv.x, 10.0, iMouse.x / iResolution.x);
    //wave += createWave(10.0, uv.x, 1000.0, 1.0);    

    float width = 10.0;

    // Colour Wave
    if(fragCoord.y < (wave + iResolution.y * 0.5) + (width * 0.5)) {
        if(fragCoord.y > (wave + iResolution.y * 0.5) - (width * 0.5)) {
            //col = vec3(1, 1, 1);
            col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
        }
    }

    // Offset backgrounds
    //float alpha = abs(sin(iTime));
    float alpha1 = sin(iTime + 1.0);
    float alpha2 = sin(iTime - 0.0);

    // Background above and below
    if(fragCoord.y < (wave + iResolution.y * 0.5) - (width * 0.5)) {
        col = (0.5 + 0.5*cos(iTime+uv.xyx+vec3(1,3,4))) * alpha1;
    }

    if(fragCoord.y > (wave + iResolution.y * 0.5) + (width * 0.5)) {
        col = (0.5 + 0.5*cos(iTime+uv.xyx+vec3(2,3,4))) * alpha2;
    }

    // Output to screen
    fragColor = vec4(col,1.0);
}
