void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    uv = fract(uv * 2.) * 2. - 1.;

    uv = sin(uv * 3.1419 * 2.);

    uv = uv * mat2(cos(iTime * 1./2.), sin(iTime * 1./3.), -sin(iTime * 1./4.), cos(iTime));

    float lower = -0.1 + -0.9 * sin(uv.x * 3.1419 * 4. + iTime * 5.);
    float upper = 0.1 + 0.9 * sin(uv.y * 3.1419 * 4.);

    float d = float(lower < uv.x && uv.x < upper && lower < uv.y && uv.y < upper);


    // Output to screen
    fragColor = vec4(d,
    (0.9 + cos(uv.x * 3.1419 * 2.) * sin(uv.y * 3.1419 * 0.5) * 0.1) * (1. - d) ,
    (0.9 + sin(uv.y * 3.1419 * 0.5) * cos(uv.x * 3.1419 * 1.) * 0.1) * (1. - d),
    1.0);
}
