// for drawing graphs
float plot (vec2 st, float pct){
  return  smoothstep( pct-0.01, pct, st.y) -
          smoothstep( pct, pct+0.01, st.y);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 st = fragCoord/iResolution.xy;
    vec3 pct = vec3(st.x);

    pct.r = min(abs((st.x - 0.6) * 1.0 / 0.3) - 1.0, 0.0) + 1.0;
    pct.g = min(1.3 - abs((st.x) * 1.0 / 0.27 - 1.3), 1.0);
    pct.b = min(1.5 - abs((st.x-0.4) * 1.0 / 0.3 - 1.5), 1.0);

    // draw graphs of red, green, blue components
    pct = mix(pct,vec3(1.0, 0.0, 0.0), plot(st, pct.r));
    pct = mix(pct,vec3(0.0, 1.0, 0.0), plot(st, pct.g));
    pct = mix(pct,vec3(0.0, 0.0, 1.0), plot(st, pct.b));

    fragColor = vec4(pct,1.0);;
}
