vec4  fC ( in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    uv /=0.08;
    uv.y /=0.64;
    uv.y +=-512.0;
    uv.x +=-18.0;
    uv.x -= iTime*-0.008;
    uv.y -= iTime*1.7;

    uv += sin(uv.x+iTime*1.7);
    uv *= uv.y+12.0+atan(uv.x+13.0, sin(uv.y))*abs(0.45*1280.0);
    uv *= inversesqrt(uv.y*90.0*30.0);
    uv *=sqrt(abs(mod(uv.y+20.0*0.2, 1280.0)));
    uv *= sin(uv.x*1.95);

    vec3 c = vec3(uv.x*1.5,uv.x*1.6,uv.x*1.9);
	return vec4(c, 1);
}

// Box-filter by Greg Rostami
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0);
    float A = 6.,  // Change A to define the level of anti-aliasing (1 to 16) ... higher numbers are REALLY slow!
          s = 1./A, x, y;

    for (x=-.5; x<.5; x+=s) for (y=-.5; y<.5; y+=s) fragColor += min ( fC(vec2(x,y)+fragCoord), 1.0);

	fragColor /= A*A;
}

