void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color = vec3(0.0);

    // Rain parameters
    float rainSpeed = 0.5;
    float rainAngle = 45.0;
    float rainThickness = 0.005;
    float rainDensity = 30.0;

    // Transform the coordinate system
    uv += iTime * rainSpeed;
    mat2 rotation = mat2(cos(rainAngle), -sin(rainAngle), sin(rainAngle), cos(rainAngle));
    uv = rotation * uv;

    // Create rain lines
    float line = fract(uv.y * rainDensity);
    float mask = step(rainThickness, line);

    // Randomness for raindrop brightness
    float randomVal = fract(sin(dot(floor(vec2(uv.x * rainDensity, uv.y * rainDensity)).xy, vec2(12.9898, 78.233))) * 43758.5453);
    color = mix(vec3(0.2, 0.4, 0.6) * 1.0, color, mask);

    fragColor = vec4(color, 1.0);
}
