float stroke (float x, float s, float w)
    { float d = step (s, x+w*0.5) - step (s, x-w*0.5) ;
    return clamp (d,0.,1.);
    }

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv =(fragCoord - .5 * iResolution.xy)/iResolution.y;
    vec2 polarUV = vec2(atan(uv.x, uv.y) , length (uv));




    // Time varying pixel color
    vec3 textureSampling = texture (iChannel0, polarUV-(iTime*.2)).rgb;
    vec3 col1 = textureSampling ;

    vec3 col2 = vec3(stroke(length(uv),0.2,0.02));
    vec3 col3 = vec3(stroke(length(uv),0.5,0.02));
    vec3 col4 = vec3(stroke(length(uv),0.7,0.02));
    vec3 col5 = vec3(stroke(length(uv),0.9,0.02));
    // Output to screen
    fragColor = vec4(col1+col2+col3+col4+col5, 1.0f);
}
