void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
   vec2 coord = 6.0 * fragCoord.xy / iResolution.xy;


    for(int n = 1; n < 50 ; n++){
        float i = float (n);
         coord += vec2(1.0 / i * sin(i * coord.y + iTime + 10.0 * i) + 0.8, 
         0.4 / i * sin(coord.x + iTime + 0.3 * i)+1.6);

    }

    vec3 color = vec3(1.0 * sin(coord.x) + 0.5, 0.5 * cos(coord.y) + 0.5, sin(coord.x + coord.y));

    fragColor = vec4(color,1.0);
}
