// thanks to https://inspirnathan.com/ for tutorial
const float PI = 3.1415926535;
const int MAX_MARCHING_STEPS = 255;
const float MIN_DIST = 0.0;
const float MAX_DIST = 100.0;
const float PRECISION = 0.001;

const mat2 m = mat2( 0.80,  0.80, -0.80,  0.80 );

float noise(in vec2 p)
{
	return sin(p.x)*cos(p.y) + sin(p.x)*sin(p.y);
}

float func(vec2 p) {
	vec2 k = vec2(noise(m*p*2.), 0.5+0.5*noise(m*p*2.));
    vec2 m = vec2(0.5+0.5*noise(m*k*2.), 0.5+0.5*noise(m*k*2.));
    float f = 0.5 + 0.5*noise(m*(4.*p + 2.0*m)*2.);
    return mix(f, f*f*f, f);
}

float rand(vec2 co) {
    return fract(sin(dot(co.xy, vec2(12.9898, 78.233))) * 43758.5453);
}

// smooth min
float smin(float a, float b, float k) {
  float h = clamp(0.5+0.5*(b-a)/k, 0.0, 1.0);
  return mix(b, a, h) - k*h*(1.0-h);
}

// smooth max
float smax(float a, float b, float k) {
  return -smin(-a, -b, k);
}

// Rotation matrix around the X axis.
mat3 rotateX(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(1, 0, 0),
        vec3(0, c, -s),
        vec3(0, s, c)
    );
}

// Rotation matrix around the Y axis.
mat3 rotateY(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, 0, s),
        vec3(0, 1, 0),
        vec3(-s, 0, c)
    );
}

// Rotation matrix around the Z axis.
mat3 rotateZ(float theta) {
    float c = cos(theta);
    float s = sin(theta);
    return mat3(
        vec3(c, -s, 0),
        vec3(s, c, 0),
        vec3(0, 0, 1)
    );
}

float distLine(vec3 ro, vec3 rd, vec3 p) {
    return length(cross(p-ro,rd))/length(rd);
}

float sdSphere (vec3 p, float r) {
	return length (p - vec3(r)) - 1.0 + 0.03 * sin(20.0*p.x + iTime);
}

float sdScene(vec3 p) {
   float sphere = smin (sdSphere (p - vec3(-3, -2, 1), 1.),   sdSphere(p - vec3(2, -2, -1.5), 1.), 3.);
   sphere = smin (sphere,   sdSphere(p - vec3(10, 5, -10), 1.), 0.5);
   return sphere;
}

float rayMarch(vec3 ro, vec3 rd, vec3 p) {
  float depth = MIN_DIST;
  p = (p) * (rotateX(iTime)*0.3 + rotateZ(iTime)*0.2 + rotateY(iTime)*0.5);
  float move = distLine(ro, rd, p);
  for (int i = 0; i < MAX_MARCHING_STEPS; i++) {
    vec3 p = ro + depth * rd;
    float d = sdScene(p);
    //depth += d;
    depth += move;
    if (d < PRECISION || depth > MAX_DIST) break;
  }

  return depth;
}

vec3 calcNormal(vec3 p) {
    vec2 e = vec2(1.0, -1.0) * 0.0005;
    return normalize(
      e.xyy * sdScene(p + e.xyy) +
      e.yyx * sdScene(p + e.yyx) +
      e.yxy * sdScene(p + e.yxy) +
      e.xxx * sdScene(p + e.xxx));
}

mat3 camera(vec3 cameraPos, vec3 lookAtPoint) {
	vec3 cd = normalize(lookAtPoint - cameraPos); // camera direction
	vec3 cr = normalize(cross(vec3(0, 1, 0), cd)); // camera right
	vec3 cu = normalize(cross(cd, cr)); // camera up

	return mat3(-cr, cu, -cd);
}

vec3 phong(vec3 lightDir, vec3 normal, vec3 rd) {
  // ambient
  float k_a = 0.6;
  vec3 i_a = vec3(0.8);
  vec3 ambient = k_a * i_a;

  // diffuse
  float k_d = 0.5;
  float dotLN = clamp(dot(lightDir, normal), 0., 1.);
  vec3 i_d = vec3(0.7, 0.5, 0);
  vec3 diffuse = k_d * dotLN * i_d;

  // specular
  float k_s = 0.6;
  float dotRV = clamp(dot(reflect(lightDir, normal), -rd), 0., 1.);
  vec3 i_s = vec3(1, 1, 1);
  float alpha = 10.;
  vec3 specular = k_s * pow(dotRV, alpha) * i_s;

  return ambient + diffuse + specular;
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
  float seed = 0.3;
  vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
  vec3 backgroundColor = vec3(rand(fragCoord) *0.2, rand(fragCoord) *0.4, rand(fragCoord) *0.4);
  float f = func(uv.xy);
  vec3 col = vec3(f, .0, .0);
  //vec3 col = vec3(0);
  vec3 lp = vec3(0.01); // lookat point (aka camera target)
  vec3 ro = vec3(0, 0, 4);

  vec3 rd = camera(ro, lp) * normalize(vec3(uv, -1)); // ray direction

  float t = iTime;
  vec3 p = vec3(sin(t),0.+sin(t)*0.5,2.+cos(t));


  float d = rayMarch(ro, rd, p);

  if (d > MAX_DIST) {
    col = backgroundColor;
  } else {
      vec3 p = ro + rd * d; // point on surface found by ray marching
      vec3 normal = calcNormal(p); // surface normal
      float randomValue = rand(fragCoord);
      if (iMouse.z > 0.) {
          if (iMouse.x > 400.) {   
             col = vec3(randomValue, randomValue * 0.3, randomValue);
             backgroundColor = vec3(f, .0, .0);
          } 
          if (iMouse.x < 400.) {   
             col = vec3(randomValue, randomValue * 0.9, randomValue * 0.3);
             backgroundColor = vec3(f, .0, .0);
          } 
      }
      // light #1
      vec3 lightPosition1 = vec3(-8, -6, -5);
      vec3 lightDirection1 = normalize(lightPosition1 - p);
      float lightIntensity1 = 0.6;

      // light #2
      vec3 lightPosition2 = vec3(1, 1, 1);
      vec3 lightDirection2 = normalize(lightPosition2 - p);
      float lightIntensity2 = 0.5;

      // final sphere color
      col += lightIntensity1 * phong(lightDirection1, normal , rd);
      col += lightIntensity2 * phong(lightDirection2, normal , rd);

  }
  fragColor = vec4(col, 1.);
}
