vec3 hsl(float h, float s, float l)
{
    vec3 rgb = clamp(abs(mod(h * 6.0 + vec3(0.0, 4.0, 2.0), 6.0) - 3.0) - 1.0, 0.0, 1.0);
    return l + s * (rgb - 0.5) * (1.0 - abs(2.0 * l - 1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 2. * (fragCoord - iResolution.xy * .5)/min(iResolution.x, iResolution.y);
    float r = length(uv) + .075;
    float a = atan(uv.x, uv.y);

    r += sin(a * 2. + r * .6 + iTime) *.1 * r;

    float rr = abs(fract(r*10.)-.5) * 4. - 1.;
    float z = sin(acos(rr));
    vec3 n = vec3(-normalize(uv)*rr, z);
    float l = clamp(dot(n, normalize(vec3(1.,1.,.25))), 0., 1.);


    vec3 col = hsl(0., 1., 0.05 + 0.95 * l*l*l);
    fragColor = vec4(col,1.0);
}
