void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 u = fragCoord / iResolution.xy;
    fragColor = vec4(fract(40.0*(u.x-u.y))<dot(round(u),vec2(0.5)), 0.0, 0.0, 1.0);
}
