/*
Estimated colors from top to bottom:
system_accent1_0
system_accent2_0
system_accent3_0
system_neutral1_0
system_neutral2_0
*/

vec3 hsl_rgb(vec3 c){
    vec3 rgb = clamp(abs(mod(c.x*6.+vec3(0,4,2),6.)-3.)-1.,0.,1.);
    return c.z+c.y*(rgb-.5)*(1.-abs(2.*c.z-1.));
}
void mainImage(out vec4 o,vec2 u){
    int v = int((u.y/iResolution.y)*5.);

    // hue, chroma and tone (also called hue, saturation and brightness)
    float hue = mod(iTime/20.,1.) + (v==2?.17:.0);
    float saturation = v==0?.08:(v==1?.04:(v==2?.24:(v==3?.16:.36))); // chroma
    float brightness = floor(u.x/iResolution.x*10.)/9.; // tone

    vec3 col = hsl_rgb(vec3(hue,saturation,brightness));
    o = vec4(col,1);
}
