const float PI = 3.141592;


const float scaleZoom  = 11111115.1 ;
const float complexity = 33. ;

const float vRot       =  .5;
const float vZoom      = .1;




vec3 prismaPalette( float t ) {

    float w = 380. + 400.*(1.-t);
    float red, green, blue;

    if (w >= 380. && w < 440.) {
        red   = -(w - 440.) / (440. - 380.);
        green = 0.;
        blue  = 1.;
    }
    else 
    if (w >= 440. && w < 490.) {
        red   = 0.;
        green = (w - 440.) / (490. - 440.);
        blue  = 1.;
    }
    else 
    if (w >= 490. && w < 510.) {
        red   = 0.;
        green = 1.;
        blue  = -(w - 510.) / (510. - 490.);
    }
    else 
    if (w >= 510. && w < 580.) {
        red   = (w - 510.) / (580. - 510.);
        green = 1.;
        blue  = 0.;
    }
    else 
    if (w >= 580. && w < 645.) {
        red   = 1.;
        green = -(w - 645.) / (645. - 580.);
        blue  = 0.;
    }
    else 
    if (w >= 645. && w < 781.) {
        red   = 1.;
        green = 0.;
        blue  = 0.;
    }
    else {
        red   = 0.;
        green = 0.;
        blue  = 0.;
    }


    // Let the intensity fall off near the vision limits
    float factor;
    if (w >= 380. && w < 420.)
        factor = .3 + .7*(w - 380.) / (420. - 380.);
    else 
    if (w >= 420. && w < 701.)
        factor = 1.;
    else 
    if (w >= 701. && w < 781.)
        factor = .3 + .7*(780. - w) / (780. - 700.);
    else
        factor = 0.;

    float gamma = .8;
    float R = (red   > 0. ? 255.*pow(red   * factor, gamma) : 0.);
    float G = (green > 0. ? 255.*pow(green * factor, gamma) : 0.);
    float B = (blue  > 0. ? 255.*pow(blue  * factor, gamma) : 0.); 

    return vec3(R/255.,G/255.,B/255.);
    //return vec3(r,0.,b);
}




vec4 function_TrantorWaves(in vec2 uv, in float t){

    t = t/1000.;

    float dist = length(uv);

    vec3  finalColor = vec3(0.0); // rgb black

    float complexLoop = complexity;//pow(complexity,1.);///log(t);

    for (float i=0.; i<complexLoop; i++) {

        float theta = 2.*PI*(i/complexLoop);
        float sin_t = sin(theta);
        float cos_t = cos(theta);

        mat2 m = mat2(    
                   cos_t, -1.5*sin_t, 
                .5*sin_t,      cos_t);

        uv *= m;
        uv = fract(1.5*uv) - .5;

        float d = length(uv) * exp(-dist);

        d = cos(8.*d + 10.*t)/.01;
        d = abs(d);
        d = pow(3.1/d, 5.2);

        float nnn = dist + 1.1*d + .1*t;
        float eee = .5+.5*log(dist)*2.2*d+.7*t; 

        float potne = (1.+cos(pow( nnn , eee )))*.5;
        potne -= (1.+sin(pow( nnn*t*t , 2.*potne)))*.5;

        vec3 col = prismaPalette(potne/.1) ;
        finalColor += col*d;

        //finalColor += col;
    }  

    return vec4(finalColor, 1.0);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {

    float x = fragCoord.x; 
    float y = fragCoord.y;
    float z = 0.;
    float t = iTime;

    bool  isLeftEye = int(y)%2==0;


    float zoom  = 1.0 + scaleZoom*(1.+sin(vZoom*t));
    float rho   = vRot*t;
    float sin_r = sin(rho);
    float cos_r = cos(rho);
    mat2  rot   = mat2( 
                   cos_r, -sin_r, 
                   sin_r,  cos_r );

    vec2  uv = (2.*fragCoord - iResolution.xy) / iResolution.y;
    uv /= zoom;  // camera zoom
    uv *= rot;   // camera rotation

    fragColor = function_TrantorWaves(uv,t);
}
